      Play:'',
      data:'',
      tärningen: [1,2,3,4,5,6]

    }

  }

  randomTärningen(){
     const randomNumber = Math.round(Math.random() * 2);
     this.setState({ data: this.state.tärningen[randomNumber] });
     console.log(randomNumber)
  }


  render() {
    return (
      <div>
        <h1>Tre Tärnings Spel</h1>

