import './App.css';
import React, { Component } from 'react'
import ShowResult from './Componet/ShowResult';
import ShowVinnare from "./Componet/ShowVinnare";

export default class App extends Component {
  constructor(){
    super();

    this.state= { 
    play:'' ,
    data:'',


    }
  }

  randomTärningar = () =>{
      const randomNumber1 = Math.ceil(Math.random() * 6 );
      const randomNumber2 = Math.ceil(Math.random() * 6 );
      const randomNumber3 = Math.ceil(Math.random() * 6 );
      const randomsum1 = randomNumber3 + randomNumber2 + randomNumber1

      const randomNumber4 = Math.ceil(Math.random() * 6 );
      const randomNumber5 = Math.ceil(Math.random() * 6 );
      const randomNumber6 = Math.ceil(Math.random() * 6 );
      const randomsum2 = randomNumber4 + randomNumber5 + randomNumber6

      this.setState({
        data: randomsum1,
        play: randomsum2
      })
  }


  render() {
    return (
      <div>
        <h1>Tre Tärnings Spel</h1><br></br>
        <ShowResult play={this.state.play} data={this.state.data}/>
        <ShowVinnare play={this.state.play} data={this.state.data} /><br></br>
        <button onClick={this.randomTärningar}>Kasta Tärningar</button>
      </div>
    )
  }
}
