import './App.css';
import React, { Component } from 'react'


export default class App extends Component {
  constructor(){
    super();

    this.state= { 
    play:'' ,
    data:'',
    tärningen: [1,2,3,4,5,6]

    }
  }

  randomTärningar = () =>{
      const randomNumber1 = Math.floor(Math.random() * 5 );
      const randomNumber2 = Math.round(Math.random() * 5 );
      const randomNumber3 = Math.round(Math.random() * 5 );
      const randomsum1 = randomNumber3 + randomNumber2 + randomNumber1
      console.log(randomNumber1)
      const randomNumber4 = Math.round(Math.random() * 5 );
      const randomNumber5 = Math.round(Math.random() * 5 );
      const randomNumber6 = Math.round(Math.random() * 5 );
      const randomsum2 = randomNumber4 + randomNumber5 + randomNumber6

      this.setState({
        data: randomsum1,
        play: randomsum2
      })
  }


  render() {
    return (
      <div>
        <h1>Tre Tärnings Spel</h1>
        <p>Data poäng:{this.state.data}</p>
        <p>Play poäng:{this.state.play}</p>
        <button onClick={this.randomTärningar}>Kasta Tärningar</button>
      </div>
    )
  }
}
